async function () {
  let totalChange = 0;
  const data = get_all_users.data.find(obj => obj.email === appsmith.store.email).leadsResponded;
  for (let i = 1; i < data.length; i++) {
    const change = data[i].y - data[i - 1].y;
    totalChange += change;
  }
  const averageChange = totalChange / (data.length - 1);
  storeValue("dailyAverageLeadsResponded", averageChange);
  return averageChange;
}

